import React from 'react';
import Card from './card.js';
// this constructor CardList becomes parent of Card constructor
//jsx item card imported can be deconstructed and the id, name, etc., passed
// robots deconstructed and passed as props to CardList

const CardList = ({ robots }) => {
	const cardComponent = robots.map((user, i) => {
		return (
			<Card
				key={i}
				id={robots[i].id}
				name={robots[0].name}
				favefood={robots[0].favefood}
				description={robots[0].description}
			/>
		);
	});
	return <div>{cardComponent}</div>;
};
export default CardList;
