import React from 'react';
// properties from robot constructor needs to be passed to the card constructor
// via a constant before the return function with properties in curly braces
// to be assigned to the param properties in the constructor

const Card = (properties) => {
	const { name, description, favefood } = properties;

	return (
		<div className="bg-gold dib br-pill pa3 ma2 grow :hover bw3 shadow-6">
			<div>
				<img src={`https://robohash.org/${properties.id}?200x200`} alt="gappy-robo" />
			</div>

			<div>
				<h2>{name}</h2>
				<p>{description}</p>
				<p>{favefood}</p>
			</div>
		</div>
	);
};

export default Card;
