import React from 'react';
// properties from robot constructor needs to be passed to the card constructor
// via a constant before the return function with properties in curly braces
// to be assigned to the param properties in the constructor

// card needs to get the parameters or properties passed from robot and index components
// this is similar to {props.name} this now becomes a dynamic name passed not static
// id is passed into the image source {properties.id}
// destructure further take out make that the constant before return and pass arguments
// since properties is already in the params take out and destructure into the main Card component
const Card = ({ name, description, favefood, id }) => {
	return (
		<div className="bg-gold dib br-pill pa3 ma2 grow tc ba bw2 shadow-6">
			<div>
				<img src={`https://robohash.org/${id}?200x200`} alt="gappy-robo" />
			</div>

			<div>
				<h2>{name}</h2>
				<p>{description}</p>
				<p>{favefood}</p>
			</div>
		</div>
	);
};

export default Card;
