import React from 'react';
// properties from robot constructor needs to be passed to the card component
// as a param/ const in the constructor and jsx render function to complete deconstruction

const Card = (properties) => {
	const { name, description, favefood } = properties;

	return (
		<div className="bg-light-pink dib br3 pa3 ma2 grow bw2 shadow-6">
			<div>
				<img src={`https://robohash.org/${properties.id}?300x300`} alt="gappy-robo" />
			</div>

			<div>
				<h2>{name}</h2>
				<p>{description}</p>
				<p>{favefood}</p>
				<button />
			</div>
		</div>
	);
};

export default Card;
