import React from 'react';
// properties from robot constructor needs to be passed to the card component
// as a param as well as a jsx item to complete deconstruction
const Card = (properties) => {
	return (
		<div className="bg-light-pink dib br3 pa3 ma2 grow bw2 shadow-5">
			<div>
				<img src={`https://robohash.org/${properties.id}?300x300`} alt="gappy-robo" />
			</div>

			<div>
				<h2>{properties.name}</h2>
				<p>{properties.description}</p>
				<p>{properties.favefood}</p>
			</div>
		</div>
	);
};

export default Card;
